import React from 'react';
import { Button, Container, Row, Col, Form ,DropdownButton,Dropdown} from 'react-bootstrap';

class StageOne extends React.Component {
    state = {
        stegeOne: true,
        selectedValue: this.props.dropDownData[0]
    }
    handleSelect =(item)=>{
        this.setState({selectedValue:item})
    }
    render() {
        const {dropDownData}=this.props;
        const {selectedValue} =this.state;
        console.log('dropDownData',dropDownData);
        return (
                <Row>
                    <Col>
                    <Dropdown>
                    <Dropdown.Toggle variant="success" id="dropdown-basic">
                       {selectedValue}
                    </Dropdown.Toggle>

                    <Dropdown.Menu >
                    {dropDownData  && dropDownData.map(item =>{
                                return (<Dropdown.Item 
                                    key={item}
                                    active={selectedValue === item }
                                    onClick={() => this.handleSelect(item)}
                                    >{item}</Dropdown.Item>)
                            })}
                    </Dropdown.Menu>
                    </Dropdown>
                    </Col>
                    <Col>
                        <Form>
                            <Form.Group as={Row} controlId="formHorizontalEmail">
                                <Form.Label column sm={4}>
                                    Selected Fruit
                                    </Form.Label>
                                <Col sm={8}>
                                    <Form.Control type="text" placeholder="Email" />
                                </Col>
                            </Form.Group>

                            <Form.Group as={Row} controlId="formHorizontalPassword">
                                <Form.Label column sm={4}>
                                    Added By
                                 </Form.Label>
                                <Col sm={8}>
                                    <Form.Control type="text" placeholder="Password" />
                                </Col>
                            </Form.Group>
                            <Form.Group as={Row} controlId="formHorizontalPassword">
                                <Form.Label column sm={4}>
                                    Date Added
                                 </Form.Label>
                                <Col sm={8}>
                                    <Form.Control type="date" placeholder="Password" />
                                </Col>
                            </Form.Group>
                            <Form.Group as={Row}>
                                <Col sm={{ span: 10, offset: 2 }}>
                                    <Button type="submit">Sign in</Button>
                                </Col>
                            </Form.Group>
                        </Form>
                    </Col>
                </Row>
        )
    }
}
export default StageOne;
