import React from 'react';
import { Button, Container, Row, Col } from 'react-bootstrap';
import StageOne from './stageOne';

class ProductPage extends React.Component {
    state={
        stegeOne:true,
        stageTwo:false
    }
    handleStages =(stage)=>{
        if(stage === 'stageOne'){
            this.setState({stegeOne:true,stageTwo:false})
        }
        else if(stage === 'stageTwo'){
            this.setState({stageTwo:true,stageOne:false})
        }
    }
    render() {
        const {stegeOne} =this.state;
        return (
            <Container>
                <Row>My Fruit Store</Row>
                <Row>
                    <Button onClick={()=> this.handleStages('stageOne')}>Page One</Button>
                    <Button onClick={()=> this.handleStages('stageTwo')}>Page Two</Button>
                </Row>
                {
                    stegeOne && (
                        <StageOne />
                    )
                }
            </Container>
        )
    }
}
export default ProductPage;
