import React from 'react';
import { Button, Container, Row, Col, Form ,DropdownButton,Dropdown} from 'react-bootstrap';

class StageOne extends React.Component {
    dateInput = React.createRef();

    handleSubmit =(event)=>{
        event.preventDefault();
        debugger;
        console.log('dateInput',this.dateInput.current.value);
        let {selectedValue,addedBy}=this.props;
        console.log('props',this.props);
        let newobj ={
            selectedValue:selectedValue,
            addedBy:addedBy,
            dateAdded:this.dateInput.current.value
        }
        this.props.handlestageOne(newobj);
    }
    handleDate = (event)=>{
        console.log('date is called',event.target.value);
    }
    render() {
        const {dropDownData,selectedValue,addedBy,handeInputChange,handleSelect}=this.props;
        // const {selectedValue,addedBy} =this.state;

        console.log('this.props',this.props);
        let selected;
        let defaultselectedFruit = dropDownData[0];
        if(selectedValue){
            selected=selectedValue
        }else {
            selected =defaultselectedFruit
        }

        return (
                <Row className="spacing">
                    <Col md={4}>
                    <Dropdown>
                    <Dropdown.Toggle variant="success" id="dropdown-basic">
                       {selected}
                    </Dropdown.Toggle>

                    <Dropdown.Menu >
                    {dropDownData  && dropDownData.map(item =>{
                                return (<Dropdown.Item 
                                    key={item}
                                    active={selected === item }
                                    onClick={() => handleSelect(item)}
                                    >{item}</Dropdown.Item>)
                            })}
                    </Dropdown.Menu>
                    </Dropdown>
                    </Col>
                    <Col md={8}>
                        <Form onSubmit={this.handleSubmit}>
                            <Form.Group as={Row} controlId="formHorizontalselectedFruit">
                                <Form.Label column sm={4} md={3} >
                                    Selected Fruit
                                    </Form.Label>
                                <Col sm={8} md={8} className="custom-input">
                                    <Form.Control type="text" placeholder="selected Fruit shown here" disabled value={selected} />
                                </Col>
                            </Form.Group>

                            <Form.Group as={Row} controlId="formHorizontaladdedby">
                                <Form.Label column sm={4} md={3}>
                                    Added By
                                 </Form.Label>
                                <Col sm={8} md={8} className="custom-input">
                                    <Form.Control type="text" placeholder="here you enter name" value={addedBy}  autoComplete="off" onChange={(event) => handeInputChange(event) }/>
                                </Col>
                            </Form.Group>
                            <Form.Group as={Row} controlId="formHorizontalPassword">
                                <Form.Label column sm={4} md={3} >
                                    Date Added
                                 </Form.Label>
                                <Col sm={8} md={8} className="custom-input">
                                    <Form.Control type="date" ref={this.dateInput} onChange={this.handleDate}/>
                                </Col>
                            </Form.Group>
                            <Form.Group as={Row}>
                                <Col sm={{ span: 3, offset: 9 }}>
                                    <Button type="submit">submit</Button>
                                </Col>
                            </Form.Group>
                        </Form>
                    </Col>
                </Row>
        )
    }
}
export default StageOne;
