import React from 'react';
import { Button, Container, Row, Col, Form ,DropdownButton,Dropdown} from 'react-bootstrap';

class StageOne extends React.Component {
    dateInput = React.createRef();
    state = {
        stegeOne: true,
        selectedValue: this.props.dropDownData[0],
        addedBy:''
    }
    handleSelect =(item)=>{
        this.setState({selectedValue:item})
    }
    handeInputChange =(event)=>{
        let value = event.target.value;
        this.setState({addedBy:value})
    }
    handleDateSelected =(value)=>{
        console.log('value',value);
    }
    handleSubmit =(event)=>{
        event.preventDefault();
        let {selectedValue,addedBy}=this.state;
        let newobj ={
            selectedValue:selectedValue,
            addedBy:addedBy
        }
        this.props.handlestageOne(newobj);
    }
    render() {
        const {dropDownData}=this.props;
        const {selectedValue,addedBy} =this.state;
        console.log('dropDownData',dropDownData);
        return (
                <Row className="spacing">
                    <Col md={4}>
                    <Dropdown>
                    <Dropdown.Toggle variant="success" id="dropdown-basic">
                       {selectedValue}
                    </Dropdown.Toggle>

                    <Dropdown.Menu >
                    {dropDownData  && dropDownData.map(item =>{
                                return (<Dropdown.Item 
                                    key={item}
                                    active={selectedValue === item }
                                    onClick={() => this.handleSelect(item)}
                                    >{item}</Dropdown.Item>)
                            })}
                    </Dropdown.Menu>
                    </Dropdown>
                    </Col>
                    <Col md={8}>
                        <Form onSubmit={this.handleSubmit}>
                            <Form.Group as={Row} controlId="formHorizontalselectedFruit">
                                <Form.Label column sm={4} md={3} >
                                    Selected Fruit
                                    </Form.Label>
                                <Col sm={8} md={8} className="custom-input">
                                    <Form.Control type="text" placeholder="selected Fruit shown here" disabled value={selectedValue} />
                                </Col>
                            </Form.Group>

                            <Form.Group as={Row} controlId="formHorizontaladdedby">
                                <Form.Label column sm={4} md={3}>
                                    Added By
                                 </Form.Label>
                                <Col sm={8} md={8} className="custom-input">
                                    <Form.Control type="text" placeholder="here you enter name" value={addedBy}  autoComplete="off" onChange={this.handeInputChange}/>
                                </Col>
                            </Form.Group>
                            <Form.Group as={Row} controlId="formHorizontalPassword">
                                <Form.Label column sm={4} md={3} >
                                    Date Added
                                 </Form.Label>
                                <Col sm={8} md={8} className="custom-input">
                                    <Form.Control type="date" ref={this.dateInput}/>
                                </Col>
                            </Form.Group>
                            <Form.Group as={Row}>
                                <Col sm={{ span: 3, offset: 9 }}>
                                    <Button type="submit">submit</Button>
                                </Col>
                            </Form.Group>
                        </Form>
                    </Col>
                </Row>
        )
    }
}
export default StageOne;
